import React, { useState } from 'react'
import axios from 'axios'
import './css/Input.css'
import { useDispatch } from 'react-redux'
import { savePokemon } from '../../../features/pokemonSlice'

export const Input = () => {

    const [ pokemonInput, setPokemonInput ] = useState('')
    const dispatch = useDispatch()

    const addPokemon = async () => {
        if (pokemonInput === '') {
            return
        }

        let response = null

        await axios.get('http://localhost:8081/pokemons/base-pokemons/' + pokemonInput)
          .then(function (res) {
            console.log('in')
            response = res.data

          })

          console.log('response', response)

        await dispatch(savePokemon({
                id: response.id,
                name: response.name
            })
        )

        setPokemonInput('')
    }

    return (
        <div className='input'>
            <input type='text' value={pokemonInput} onChange={e => setPokemonInput(e.target.value) } />
            <button onClick={addPokemon}>Add</button>
        </div> 
    )
}
export default Input
